/*
Jeremy Goldstein
Minuteman Library Network

search for order records by ISBN used within ISBN pick field
*/

SELECT
rm.record_type_code||rm.record_num||'a' AS onumber,
SPLIT_PART(v.field_content,'|b',1) AS isbn_pick,
SPLIT_PART(v.field_content,'|b',2) AS sent_date,
o.order_status_code
FROM
sierra_view.order_record o
JOIN
sierra_view.varfield v
ON
o.id = v.record_id AND v.varfield_type_code = 'b' AND o.accounting_unit_code_num = '30'
JOIN
sierra_view.record_metadata rm
ON
o.id = rm.id

WHERE SPLIT_PART(v.field_content,'|',1) IN
(
